import { addAudio, getAudios } from '../../helpers'


export const Audio = ({ audio }) => {

    const addToArray = () => {
        if (value === "False") {
            deleteAudio(audio);
        } else {
            addAudio(audio);
        }

    }

    return (
        <div className="custom-checkbox" onClick={addToArray}  >
            <label className="custom-checkbox">
                <input type="hidden" name={audio.name} value="False" />
                <input key={audio._id} class="custom-checkbox-input" name={audio.name} value="True" type="checkbox" />
                <h3 className="custom-checkbox-text">{audio.name}</h3>
            </label>
        </div>
    )
}

