import React, { useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlayCircle, faAngleLeft, faAngleRight, faStopCircle } from '@fortawesome/free-solid-svg-icons';


export const Player = ({
    audioRef, currentSong, isPlaying, setIsPlaying,
    setSongInfo, songInfo, songs, setCurrentSong, setSongs }) => {


    // Format Time

    const getTime = (time) => {
        return (
            Math.floor(time / 60) + ":" + ("0" + Math.floor(time % 60)).slice(-2)
        )
    }


    // useEffect for active Song when skipping with library

    useEffect(() => {


        const newSongs = songs.map((song) => {
            if (song.id === currentSong.id) {

                return {
                    ...song,
                    active: true,
                }
            } else {
                return {
                    ...song,
                    active: false
                };
            }

        });

        setSongs(newSongs);
    }, [currentSong])

    //Event Handlers
    const playSongHandler = () => {
        audioRef.current.play();
        if (isPlaying) {
            audioRef.current.pause();
            setIsPlaying(!isPlaying);

        } else {
            audioRef.current.play();
            setIsPlaying(!isPlaying);
        }
    }


    const dragHandler = (e) => {
        audioRef.current.currentTime = e.target.value;
        setSongInfo({ ...songInfo, currentTime: e.target.value });
    }


    const skipTrackHandler = (flow) => {

        let currentIndex = songs.findIndex((song) => song.id === currentSong.id);
        if (flow === 'skip-forward') {
            setCurrentSong(songs[(currentIndex + 1) % songs.length])
        }
        if (flow === 'skip-back') {
            if ((currentIndex - 1) % songs.length === -1) {
                setCurrentSong(songs[songs.length - 1]);
                return;
            }
            setCurrentSong(songs[(currentIndex - 1) % songs.length])
        }
    }

    return (
        <div className="player">
            <div className="timeControl">
                <p>{getTime(songInfo.currentTime)}</p>
                <input onChange={dragHandler} min={0} max={songInfo.duration} value={songInfo.currentTime} type="range" />
                <p>{getTime(songInfo.duration || 0)}</p>
            </div>
            <div className="playControl">
                <FontAwesomeIcon onClick={() => skipTrackHandler('skip-back')} className="skip-back" size="2x" icon={faAngleLeft} />
                <FontAwesomeIcon onClick={playSongHandler} className="play" size="3x" icon={isPlaying ? faStopCircle : faPlayCircle} />
                <FontAwesomeIcon onClick={() => skipTrackHandler('skip-forward')} className="skip-forward" size="2x" icon={faAngleRight} />
            </div>

        </div>
    );
}
