import React, { useRef } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlayCircle, faAngleLeft, faAngleRight } from '@fortawesome/free-solid-svg-icons';

export const Player = ({ currentSong, isPlaying }) => {

    // we use Ref
    const audioRef = useRef(null);


    //Event Handlers
    const playSongHandler = () => {
        audioRef.current.play();
        if (isPlaying === false) {
            audioRef.current.stop();
        }
    }

    return (
        <div className="player">
            <div className="timeControl">
                <p>Start Time</p>
                <input type="range" />
                <p>End Time</p>
            </div>
            <div className="playControl">
                <FontAwesomeIcon className="skip-back" size="2x" icon={faAngleLeft} />
                <FontAwesomeIcon onClick={playSongHandler} className="play" size="3x" icon={faPlayCircle} />
                <FontAwesomeIcon className="skip-forward" size="2x" icon={faAngleRight} />
            </div>

            <audio ref={audioRef} src={currentSong.audio}></audio>
        </div>
    );
}
