import { CategoriesElements } from '../../components/Categories/CategoriesElements';
import { BrowserRouter, Route, Switch, Link, Router } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
import { Tupase } from '../Categories/Tupase';
import { SearchBox } from '../Categories/SearchBox';
import { CreatorAuthor } from '../../components/Categories/Author';
import slugify from 'slugify';



export const CreatorComparsa = () => {

    const [comparsa, setComparsa] = useState([]);
    const [author, setAuthor] = useState([])
    const [error, setError] = useState("");

    useEffect(() => {
        getComparsa();
        getAuthor();
    }, [])


    // A MANO
    const requestOptions = {
        method: 'GET',
        headers: {
            'Accept': 'application/json',
            'Access-Control-Allow-Origin': '*',
            'Content-Type': 'applications/json',
            'Mode': 'no-cors'
        }
    };

    const getComparsa = () => {
        return fetch("http://localhost:3002/api/tipo/show", {
            method: "GET",
        })
            .then((response) => {
                return response.json();
            })
            .then((data) => {
                if (data.error) {
                    setError(data.error);
                } else {
                    setComparsa(data);
                }
            })
            .catch((error) => {
                console.log(error);
            });
    };

    const getAuthor = () => {
        return fetch("http://localhost:3002/api/author", {
            method: "GET",
        })
            .then((response) => {
                return response.json();
            })
            .then((data) => {
                if (data.error) {
                    setError(data.error);
                } else {
                    setAuthor(data);
                }
            })
            .catch((error) => {
                console.log(error);
            });
    };

    return (
        <>

            <div className="creatorRow">
                <SearchBox />
                <Tupase children="COMPARSA" />
                <div className="cardsPosition">
                    {comparsa.filter(c => (c.final === true && c.modalidad === "Comparsa")).map(c => (
                        <Link key={a._id} to={`/creator/author/${a._id}`} ><CategoriesElements myAuthor={c.author} children={c.name} image={<img src={c.picture} alt="firstPrize" width="375px" height="225px" object-fit="cover" />} /></Link>
                    ))}
                </div>
            </div>

        </>
    );
}
