import React, { useRef, useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlayCircle, faAngleLeft, faAngleRight, faStopCircle } from '@fortawesome/free-solid-svg-icons';

export const Player = ({ currentSong, isPlaying, setIsPlaying }) => {

    // we use Ref
    const audioRef = useRef(null);

    // Format Time

    const getTime = (time) => {
        return (
            Math.floor(time / 60) + ":" + ("0" + Math.floor(time % 60)).slice(-2)
        )
    }
    //State
    const [songInfo, setSongInfo] = useState({
        currentTime: 0,
        duration: 0,
    });

    //Event Handlers
    const playSongHandler = () => {
        audioRef.current.play();
        if (isPlaying) {
            audioRef.current.pause();
            setIsPlaying(!isPlaying);
            // icon = { faStopCircle };
        } else {
            audioRef.current.play();
            setIsPlaying(!isPlaying);
        }
    }

    const timeUpdateHandler = (e) => {

        const current = e.target.currentTime;
        const duration = e.target.duration;

        setSongInfo({ ...songInfo, currentTime: current, duration })
    }

    const dragHandler = (e) => {
        audioRef.current.currentTime = e.target.value;
        setSongInfo({ ...songInfo, currentTime: e.target.value });
    }


    return (
        <div className="player">
            <div className="timeControl">
                <p>{getTime(songInfo.currentTime)}</p>
                <input onChange={dragHandler} min={0} max={songInfo.duration} value={songInfo.currentTime} type="range" />
                <p>{getTime(songInfo.duration)}</p>
            </div>
            <div className="playControl">
                <FontAwesomeIcon className="skip-back" size="2x" icon={faAngleLeft} />
                <FontAwesomeIcon onClick={playSongHandler} className="play" size="3x" icon={faPlayCircle} />
                <FontAwesomeIcon className="skip-forward" size="2x" icon={faAngleRight} />
            </div>

            <audio onLoadedMetadata={timeUpdateHandler} onTimeUpdate={timeUpdateHandler} ref={audioRef} src={currentSong.audio}></audio>
        </div>
    );
}
