import { SearchBox } from './SearchBox';

import { Tupase } from './Tupase';
import { Button } from '@material-ui/core';
import { Audio } from './Audio';
import { addAudio, deleteAudio, getAudios } from '../../helpers';

import { Switch, Link, Router, useLocation } from 'react-router-dom';
import React, { useState, useEffect } from 'react';

export const CreatorAudio = ({ }) => {
    const [tipo, setTipo] = useState([]);
    const [audio, setAudio] = useState([]);
    const [error, setError] = useState("");
    const [customPase, setCustomPase] = useState([]);

    useEffect(() => {
        getAudio();
        getTipo();
    }, [])


    // A MANO
    const requestOptions = {
        method: 'GET',
        headers: {
            'Accept': 'application/json',
            'Access-Control-Allow-Origin': '*',
            'Content-Type': 'applications/json',
            'Mode': 'no-cors'
        }
    };


    const getAudio = () => {
        return fetch("http://localhost:3002/api/audio/show", {
            method: "GET",
        })
            .then((response) => {
                return response.json();
            })
            .then((data) => {
                if (data.error) {
                    setError(data.error);
                } else {
                    setAudio(data);
                }
            })
            .catch((error) => {
                console.log(error);
            });
    };

    const getTipo = () => {
        return fetch(`http://localhost:3002/api/tipo/show`, {
            method: "GET",
        })
            .then((response) => {
                return response.json();
            })
            .then((data) => {
                if (data.error) {
                    setError(data.error);
                } else {
                    setTipo(data);
                }
            })
            .catch((error) => {
                console.log(error);
            });
    };

    const tipoLocation = useLocation().pathname.split("/")[4];

    return (
        <>
            <div className="creator">
                <div className="creatorRow">
                    <div className="topHeader">
                        <SearchBox />
                        {tipo.filter(tipo => tipo._id === tipoLocation).map(audio => (
                            <Tupase children={(audio.name).replace(/\s+/g, '').toUpperCase()} />))}
                    </div>
                    <div className="authores">
                        <div className="authorCard" >
                            {audio.map(audio => (
                                <Audio key={audio._id} audio={audio} />
                            ))}
                            <Button onClick={getAudios} variant="contained" color="primary">START</Button>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}
