import '../../styles/styles.scss';
import { Button, Card, Paper, Typography, CardContent, CardActions } from '@material-ui/core';
import React, { useEffect, useState } from "react";


export const NewsDashboard = () => {

    const [news, setNews] = useState([]);
    const [error, setError] = useState("");

    useEffect(() => {
        getNews();
    }, [])


    // A MANO
    const requestOptions = {
        method: 'GET',
        headers: {
            'Accept': 'application/json',
            'Access-Control-Allow-Origin': '*',
            'Content-Type': 'applications/json',
            'Mode': 'no-cors'
        }
    };


    const getNews = () => {
        return fetch("http://localhost:3002/api/news/show", {
            method: "GET",
        })
            .then((response) => {
                return response.json();
            })
            .then((data) => {
                if (data.error) {
                    setError(data.error);
                } else {
                    setNews(data);
                }
            })
            .catch((error) => {
                console.log(error);
            });
    };
    /*   <>
      <div className="newsDashboard">
          <div className="newsInfo">
              {news.map(news => (
                  <div>
                      <div className="">
                          <div className="">{news.title}</div>
                          <div className="">{news.subtitle}</div>
                          <div className="">{news.body}</div>
                      </div>
                      <div classname="newsPic">

                      </div>
                  </div>
              ))}
              <Button className="newsButton">CONTINUAR...</Button>
          </div >
      </div >
  </> */



    return (
        <>
            <div className="newsDashboard">
                <div className="newsInfo">
                    {news.map(news => (
                        <>
                            <div className="newsText">
                                <div className="newsTitle">{news.title}</div>
                                <div className="newsSubtitle">{news.subtitle}</div>
                                <div className="newsBody">{news.body}</div>
                                <div className="newsBody">{news.body}</div>

                                <Button className="newsButton">CONTINUAR...</Button>
                            </div>
                            <div className="newsPic">
                                <div ><img src={news.picture} alt={news.title} /></div>
                            </div>
                        </>
                    ))}

                </div>
            </div>
        </>
    )

}
