import '../../styles/styles.scss';
import { Button, Card, Paper, Typography, CardContent, CardActions } from '@material-ui/core';
import React, { useEffect, useState } from "react";


export const NewsDashboard = () => {

    const [news, setNews] = useState([]);
    const [error, setError] = useState("");

    useEffect(() => {
        getNews();
    }, [])


    // A MANO
    const requestOptions = {
        method: 'GET',
        headers: {
            'Accept': 'application/json',
            'Access-Control-Allow-Origin': '*',
            'Content-Type': 'applications/json',
            'Mode': 'no-cors'
        }
    };


    const getNews = () => {
        return fetch("http://localhost:3002/api/news/show", {
            method: "GET",
        })
            .then((response) => {
                return response.json();
            })
            .then((data) => {
                if (data.error) {
                    setError(data.error);
                } else {
                    setNews(data);
                }
            })
            .catch((error) => {
                console.log(error);
            });
    };



    return (
        <>
            {
                news.map(n =>
                    <div className="newsDashboard">
                        <div className="newsInfo">

                            <div className="newsTitle"><span><h1>{n.title}</h1></span></div>
                            <span className="newsText"></span>
                            <span className="newsHeader"><h4></h4></span>
                            <span className="newsText"></span>
                            <Button className="newsButton">CONTINUAR...</Button>
                            <div className="newsPicture"></div>
                        </div>
                    </div>)
            }
        </>
    )

}
