import React, { useState } from 'react';

export const PlaylistTrack = ({ pases, userPlaylist }) => {
    const [loadPase, setLoadPase] = useState(false);


    const addTrack = () => {
        console.log(userPlaylist);
        userPlaylist = pases.audios;
        console.log(pases.audios);
        /* pases.audios.forEach(element => {
            userPlaylist.push(element.mp3)
        })
        console.log(userPlaylist); */

        userPlaylist = Array.from(new Set(userPlaylist.map((p) => p._id))).map((id) => {
            userPlaylist.find((p) => p._id === id);
            const newPlaylist = JSON.stringify(userPlaylist);
            console.log(newPlaylist);
            localStorage.setItem("audios", newPlaylist)
            return newPlaylist;
        });




    }

    const deleteTrack = (audiosId) => {
        userPlaylist.map((pases, index) => {
            if (pases._id === audiosId) {
                userPlaylist.splice(index, 1);
            }

        });
        const newPlaylist = JSON.stringify(userPlaylist);
        localStorage.setItem("audios", newPlaylist);
        return JSON.stringify(userPlaylist);


    }


    const togglePase = () => {
        if (loadPase === false) {
            addTrack(pases);
            setLoadPase(true);
        }
        else {
            deleteTrack(pases._id);
            setLoadPase(false);
        }

    }
    return (
        <div className="customContainer">
            <div className="customPase">
                <div className="imgBox">
                    <img src={pases.picture} alt={pases.tipo} className="center" />
                </div>
                <div className="infoBox">
                    <h4>{pases.tipo}
                        <h4>{pases.year}</h4>
                    </h4>
                    <div className="infoToggle" id={pases._id} onClick={togglePase}>
                        <input type="checkbox" name="" />
                    </div>
                </div>
            </div>
        </div>)
}
