import { CategoriesElements } from './CategoriesElements';
import { SearchBox } from './SearchBox';
import { Sidebar } from '../Sidebar/Sidebar';
import { Tupase } from './Tupase';

import { Switch, Link, Router } from 'react-router-dom';
import React, { useState, useEffect } from 'react';


export const CreatorAuthor = (key) => {

    const [author, setAuthor] = useState([]);
    const [error, setError] = useState("");

    useEffect(() => {
        getAuthor();
    }, [])


    // A MANO
    const requestOptions = {
        method: 'GET',
        headers: {
            'Accept': 'application/json',
            'Access-Control-Allow-Origin': '*',
            'Content-Type': 'applications/json',
            'Mode': 'no-cors'
        }
    };

    const getAuthor = () => {
        return fetch("http://localhost:3002/api/author/show", {
            method: "GET",
        })
            .then((response) => {
                return response.json();
            })
            .then((data) => {
                if (data.error) {
                    setError(data.error);
                } else {
                    setAuthor(data);
                }
            })
            .catch((error) => {
                console.log(error);
            });
    };

    return (
        <>
            <div className="creator">
                <div className="creatorRow">
                    <div className="topHeader">
                        <SearchBox />
                        <Tupase children="COMPARSA" />
                    </div>
                    <div className="authores">
                        {author.filter(author => author.modalidad._id === key).map(author => (
                            <div className="authorCard" key={author._id}>
                                <img src={author.photo} alt={author.name} />
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </>
    );
}
