import React from 'react';

export const Audio = ({ audio }) => {
    return (
        <>
            <label class="custom-checkbox">

                <input value={audio.mp3} type="hidden" name={audio.name} value="False" />
                <input key={audio._id} class="custom-checkbox-input" name={audio.name} value="True" type="checkbox" />
                <span class="custom-checkbox-text">{audio.name}</span>
                <a href={audio.mp3}>Enlace</a>

            </label>
        </>
    )
}


//add product to the local storage
export const addProduct = (item) => {
    let cart = [];
    if (typeof window !== "undefined") {
        if (localStorage.getItem("cart")) {
            cart = JSON.parse(localStorage.getItem("cart"));
        }
        cart.push({
            ...item
        });

        cart = Array.from(new Set(cart.map((p) => p._id))).map((id) => {
            return cart.find((p) => p._id === id);
        });

        localStorage.setItem("cart", JSON.stringify(cart));
    }
};


//get chosen products from local storage
export const getCartProducts = () => {
    if (typeof window !== "undefined") {
        if (localStorage.getItem("cart")) {
            return JSON.parse(localStorage.getItem("cart"));
        }
    }
    return [];
};
