import { Button } from '@material-ui/core';
import React, { useState, useEffect } from 'react';
import { UserLogin } from '../User/UserLogin';
import { UserRegister } from '../User/UserRegister';


export const UserUnknown = () => {

    const [register, setRegister] = useState(false);
    const [login, setLogin] = useState(false);
    const [start, setStart] = useState(true);

    const registerHandler = () => {
        setRegister(!register)
        setStart(!start)
    }

    const loginHandler = () => {
        setLogin(!login)
        setStart(!start)
    }




    return (
        <>
            {/*  <div className="user">
                <div className="userLogin">
                    <div className="loginDetails">
                        <span className="loginTitle">Bienvenido,
                            carnavalero</span>
                    </div>
                    {start && <div className="loginSubtitle"><span>See all the new features we’ve added through our tutorial.</span>
                        <span>See all the new features we’ve added through our tutorial.</span></div>}
                    {login ? <UserLogin /> : ""}
                    {register ? <UserRegister /> : ""}


                    {start && <Button onClick={registerHandler} variant="contained" color="primary">Registrarse</Button>}
                    {start && <Button onClick={loginHandler} variant="contained" color="primary">Iniciar sesión</Button>}

                </div>
            </div> */}
            <div className="user">
                <div className="userLogin">
                    <div className="loginDetails">
                        <span className="loginTitle">Bienvenido,
                            carnavalero</span>
                    </div>
                    <>
                        {!login && (<div className="loginSubtitle"><span>See all the new features we’ve added through our tutorial.</span>
                            <span>See all the new features we’ve added through our tutorial.</span></div>)}

                    </>
                    <>
                        {register != true && (<form className="loginForm">
                            <div className="loginInfo"><label>Username</label>
                                <input type="text" /></div>
                            <div className="loginInfo"><label>Email</label>
                                <input type="mail" /></div>
                            <div className="loginInfo"><label>Password</label>
                                <input type="password" /></div>
                            <submit><Button variant="contained" color="primary">Registrarse</Button></submit>

                        </form>)}
                        {!login && (<form className="loginForm">
                            <div className="loginInfo"><label>Email</label>
                                <input type="mail" /></div>
                            <div className="loginInfo"><label>Password</label>
                                <input type="password" /></div>
                            <submit><Button variant="contained" color="primary">Registrarse</Button></submit>

                        </form>)}

                    </>


                    <div className="loginButtons">

                        {!login && <Button variant="contained" color="primary">Registrarse</Button>}
                        {register === true && <Button variant="contained" color="primary">Iniciar sesión</Button>}
                    </div>
                </div>
            </div>


        </>
    );
}
