import { addAudio, getAudios } from '../../helpers'


export const Audio = ({ audio }) => {

    const addToArray = () => {
        addAudio(audio);
    }

    return (

        <label onClick={addToArray} className="custom-checkbox">
            <input value={audio} type="hidden" name={audio.name} value="False" />
            <input key={audio._id} class="custom-checkbox-input" name={audio.name} value="True" type="checkbox" />
            <h3 className="custom-checkbox-text">{audio.name}</h3>
        </label>

    )
}

