import React, { useState } from 'react';

export const PlaylistTrack = ({ pases, userPlaylist }) => {
    const [loadPase, setLoadPase] = useState(false);
    const [checkTrack, setCheckTrack] = useState(2);

    const ReaddTrack = (pases) => {
        console.log(pases);
        if (checkTrack === 2) {
            const lastArray = localStorage.getItem("audios");
            const paseToArray = pases.audios;
            const newArray = [...lastArray, paseToArray];

            localStorage.setItem("audios", JSON.stringify(newArray));
            return newArray;
        }
        if (checkTrack === 1) {
            console.log("ADIOS");

            const lastArray = pases.audios;
            localStorage.setItem("audios", JSON.stringify(lastArray))
            return lastArray;
        }

    };
    const RedeleteTrack = (audiosId) => {

        userPlaylist.map((pases, index) => {
            if (pases._id === audiosId) {
                userPlaylist.splice(index, 1);
            }
        });
        return userPlaylist;
        /* const newPlaylist = JSON.stringify(userPlaylist);
        localStorage.setItem("audios", newPlaylist);
        return JSON.stringify(userPlaylist); */

    }

    const togglePase = () => {
        if (loadPase === true) {
            RedeleteTrack(pases._id);
            setLoadPase(false);
            console.log(userPlaylist);
        }
        else {
            ReaddTrack(pases);
            setLoadPase(true);
            console.log(userPlaylist);
        }

    }
    return (
        <div className="customContainer">
            <div className="customPase">
                <div className="imgBox">
                    <img src={pases.picture} alt={pases.tipo} className="center" />
                </div>
                <div className="infoBox">
                    <h4>{pases.tipo}
                        <h4>{pases.year}</h4>
                    </h4>
                    <div className="infoToggle" id={pases._id} onClick={togglePase}>
                        <input type="checkbox" name="" />
                    </div>
                </div>
            </div>
        </div>)
}
