import { addAudio, deleteAudio, getAudios } from '../../helpers';
import React, { useState } from 'react';


export const Audio = ({ audio }) => {

    const [checkAudio, setCheckAudio] = useState(true);
    const [customPase, setCustomPase] = useState([]);

    const addAudio = (audio) => {

        if (typeof window !== "undefined") {
            if (localStorage.getItem("audios")) {
                customPase = JSON.parse(localStorage.getItem("audios"));
            }
            customPase.push([
                ...audio

            ]);

            audios = Array.from(new Set(audios.map((p) => p._id))).map((id) => {
                return audios.find((p) => p._id === id);
            });

            localStorage.setItem("audios", JSON.stringify(audios));
        }
    };
    const editArray = () => {
        if (checkAudio === false) {
            deleteAudio(audio._id);
            setCheckAudio(true);
        }
        else {
            addAudio(audio);
            setCheckAudio(false);
        }

    }

    return (
        <div className="custom-checkbox" onChange={editArray}  >
            <label className="custom-checkbox">
                <input value={audio.mp3} type="hidden" name={audio.name} value="False" />
                <input key={audio._id} class="custom-checkbox-input" name={audio.name} value="True" type="checkbox" />
                <h3 className="custom-checkbox-text">{audio.name}</h3>
            </label>
        </div>
    )

}
