import { CategoriesElements } from './CategoriesElements';
import { SearchBox } from './SearchBox';
import { Sidebar } from '../Sidebar/Sidebar';
import { Tupase } from './Tupase';

import { Switch, Link, Router, useLocation } from 'react-router-dom';
import React, { useState, useEffect } from 'react';



export const CreatorAudio = ({ }) => {
    const [tipo, setTipo] = useState([]);
    const [audio, setAudio] = useState([]);
    const [error, setError] = useState("");

    useEffect(() => {
        getAudio();
        getTipo();
    }, [])


    // A MANO
    const requestOptions = {
        method: 'GET',
        headers: {
            'Accept': 'application/json',
            'Access-Control-Allow-Origin': '*',
            'Content-Type': 'applications/json',
            'Mode': 'no-cors'
        }
    };


    const getAudio = () => {
        return fetch("http://localhost:3002/api/audio/show", {
            method: "GET",
        })
            .then((response) => {
                return response.json();
            })
            .then((data) => {
                if (data.error) {
                    setError(data.error);
                } else {
                    setAudio(data);
                }
            })
            .catch((error) => {
                console.log(error);
            });
    };

    const getTipo = () => {
        return fetch(`http://localhost:3002/api/tipo/show`, {
            method: "GET",
        })
            .then((response) => {
                return response.json();
            })
            .then((data) => {
                if (data.error) {
                    setError(data.error);
                } else {
                    setTipo(data);
                }
            })
            .catch((error) => {
                console.log(error);
            });
    };

    const tipoLocation = useLocation().pathname.split("/")[4];
    const Checkbox = props => (
        <input type="checkbox" {...props} />
    )
    class MyButton extends React.Component {
        state = { checked: false }
        handleCheckboxChange = (event, test) =>
            this.setState({ checked: event.target.checked })
        render() {
            return (
                <div>
                    <label>
                        <Checkbox
                            checked={this.state.checked}
                            onChange={this.handleCheckboxChange}
                        />
                        <span>({test})</span>
                    </label>
                </div>
            )
        }
    }



    return (
        <>
            <div className="creator">
                <div className="creatorRow">
                    <div className="topHeader">
                        <SearchBox />
                        {tipo.filter(tipo => tipo._id === tipoLocation).map(audio => (
                            <Tupase children={(audio.name).replace(/\s+/g, '').toUpperCase()} />))}
                    </div>
                    <div className="authores">

                        {audio.map(audio => (
                            <div className="authorCard" key={audio._id}>
                                <Button test="HOLA"><input href={audio.mp3}>{audio.name}</input></Button>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </>
    );
}
