import React, { useState, useEffect } from 'react';

export const PlaylistTrack = ({ pases }) => {
    const [loadPase, setLoadPase] = useState(false);
    const [trackToggle, setTrackToggle] = useState(false);
    console.log(localNow);

    const checkStatus = () => {
        if (localStorage.getItem("audios")) {
            const checked = localStorage.getItem("audios").filter(element => element.tipos === pases.tipo);
            console.log(checked);
            return checked;

        }
        else {
            const checked = "";
            console.log(checked);
            return checked;
        }
        /* if (checked != "") {
            setTrackToggle(true);
        } */


    }
    useEffect(() => {
        checkStatus();
    }, [])
    console.log(pases);
    console.log(localStorage.getItem("audios"));




    const ReaddTrack = (pases) => {

        if (localStorage.getItem("audios")) {
            const lastArray = JSON.parse(localStorage.getItem("audios"));

            const paseToArray = pases.audios;

            const tipo = pases.tipo;
            const picture = pases.picture;

            paseToArray.forEach(element => {
                const thirdObject = { ...element, picture, tipo }
                lastArray.push(thirdObject);
            });

            localStorage.setItem("audios", JSON.stringify(lastArray));
            return lastArray;
        }
        else {
            const lastArray = [];
            let firstArray = pases.audios;
            const tipos = pases.tipo;
            const picture = pases.picture;

            firstArray.forEach(element => {
                const thirdObject = { ...element, picture, tipos }
                lastArray.push(thirdObject);

            });
            localStorage.setItem("audios", JSON.stringify(lastArray))
            return lastArray;

        }

    };
    const RedeleteTrack = (tipoId) => {

        const Local = JSON.parse(localStorage.getItem("audios"));
        console.log(Local);
        Local.map((elem, index) => {
            if (elem.tipos === tipoId) {
                Local.splice(index, 1);
            }
        });
        return localStorage.setItem("audios", JSON.stringify(Local));

    }

    const togglePase = () => {
        if (loadPase === true) {
            RedeleteTrack(pases.tipo);
            setLoadPase(false);
            console.log(localStorage.getItem("audios"));
        }
        else {
            ReaddTrack(pases);
            setLoadPase(true);
            console.log(JSON.parse(localStorage.getItem("audios")));
        }

    }
    return (
        <div className="customContainer">
            <div className="customPase">
                <div className="imgBox">
                    <img src={pases.picture} alt={pases.tipo} className="center" />
                </div>
                <div className="infoBox">
                    <h4>{pases.tipo}
                        <h4>{pases.year}</h4>
                    </h4>
                    <div className="infoToggle" id={pases._id} onClick={togglePase}>
                        <input type="checkbox" name="" />
                    </div>
                </div>
            </div>
        </div>)
}
