    return (
        <>
            <div className="children">
                <Card className={classes.root} variant="outlined">
                    <CardContent>
                        {cards.map(card => {
                            <div className="card">
                                <img src={card.img} alt="" />
                                <h3>{card.title}</h3>
                            </div>
                        })}
                    </CardContent>
                    <CardActions>
                        <Button size="small">{children}</Button>

                    </CardActions>
                </Card>
            </div>
        </>
    )
}



export default CategoriesElements;
