import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlayCircle, faAngleLeft, faAngleRight } from '@fortawesome/free-solid-svg-icons';

export const Player = ({ currentSong }) => {
    return (
        <div className="player">
            <div className="timeControl">
                <p>Start Time</p>
                <input type="range" />
                <p>End Time</p>
            </div>
            <div className="playControl">
                <FontAwesomeIcon className="skip-back" size="2x" icon={faAngleLeft} />
                <FontAwesomeIcon onClick={playSongHandler} className="play" size="3x" icon={faPlayCircle} />
                <FontAwesomeIcon className="skip-forward" size="2x" icon={faAngleRight} />
            </div>

            <audio src={currentSong.audio}></audio>
        </div>
    );
}
