import { Button } from '@material-ui/core';
import React, { useState, useEffect } from 'react';
import { authentication, mySignUp } from '../../helpers';
import { useHistory } from 'react-router-dom';


export const UserRegister = () => {
    const [register, setRegister] = useState(true);
    const [login, setLogin] = useState(true);

    const registerHandler = () => {
        setRegister(!register)
    }

    const loginHandler = () => {
        setLogin(false)
    }

    const [signUpOK, setSignUpOK] = useState({
        username: "",
        email: "",
        password: "",
        error: ""
    });
    const history = useHistory();

    const handleInputs = (name) => event => {
        setSignUpOK({ ...signUpOK, error: false, [name]: event.target.value })
    }

    const signUpSubmit = (event) => {
        event.preventDefault();

        mySignUp(signUpOK)
            .then(data => {
                if (data.error) {
                    setSignUpOK({ ...signUpOK, error: data.error })
                }
                else {
                    authentication(data, () => {
                        setSignUpOK({
                            username: "",
                            email: "",
                            password: "",
                            error: ""
                        })
                        history.push("/user/login")
                    })

                }
            })

    }


    return (
        <>






            <div className="loginButtons">
                <form className="loginForm">
                    <div className="loginInfo"><label>Username</label>
                        <input type="text" /></div>
                    <div className="loginInfo"><label>Email</label>
                        <input type="mail" /></div>
                    <div className="loginInfo"><label>Password</label>
                        <input type="password" /></div>
                    <Button type="submit" variant="contained" color="primary">Registrarse</Button>

                </form>

            </div>



        </>
    );
}
