import { CategoriesElements } from './CategoriesElements';
import { SearchBox } from './SearchBox';
import { Sidebar } from '../Sidebar/Sidebar';
import { Tupase } from './Tupase';
import { Button } from '@material-ui/core';


import { Switch, Link, Router, useLocation } from 'react-router-dom';
import React, { useState, useEffect } from 'react';



export const CreatorAudio = ({ }) => {
    const [tipo, setTipo] = useState([]);
    const [audio, setAudio] = useState([]);
    const [error, setError] = useState("");

    useEffect(() => {
        getAudio();
        getTipo();
    }, [])


    // A MANO
    const requestOptions = {
        method: 'GET',
        headers: {
            'Accept': 'application/json',
            'Access-Control-Allow-Origin': '*',
            'Content-Type': 'applications/json',
            'Mode': 'no-cors'
        }
    };


    const getAudio = () => {
        return fetch("http://localhost:3002/api/audio/show", {
            method: "GET",
        })
            .then((response) => {
                return response.json();
            })
            .then((data) => {
                if (data.error) {
                    setError(data.error);
                } else {
                    setAudio(data);
                }
            })
            .catch((error) => {
                console.log(error);
            });
    };

    const getTipo = () => {
        return fetch(`http://localhost:3002/api/tipo/show`, {
            method: "GET",
        })
            .then((response) => {
                return response.json();
            })
            .then((data) => {
                if (data.error) {
                    setError(data.error);
                } else {
                    setTipo(data);
                }
            })
            .catch((error) => {
                console.log(error);
            });
    };

    const tipoLocation = useLocation().pathname.split("/")[4];
    const [addAudio, setAddAudio] = useState([]);

    class Hello extends React.Component {
        constructor(props) {
            super(props);
            this.state = [];
            this.addValue = this.addValue.bind(this);
            this.updateInput = this.updateInput.bind(this);
        }

        addValue(evt) {
            evt.preventDefault();

            let tasks = this.state.tasks;
            let addtask = this.state.addtask;
            tasks.push({ name: addtask });
            this.setState({ tasks: tasks });
            console.log(tasks);
        }
        updateInput(evt) {
            this.setState({ addtask: evt.target.value });
        }

    }


    return (
        <>
            <div className="creator">
                <div className="creatorRow">
                    <div className="topHeader">
                        <SearchBox />
                        {tipo.filter(tipo => tipo._id === tipoLocation).map(audio => (
                            <Tupase children={(audio.name).replace(/\s+/g, '').toUpperCase()} />))}
                    </div>
                    <div className="authores">
                        <div className="authorCard" >

                            <form action="" method="POST">
                                {audio.map(audio => (
                                    <>
                                        <label class="custom-checkbox">
                                            <input type="hidden" name={audio.name} value="False" />
                                            <input key={audio._id} class="custom-checkbox-input" name={audio.name} value="True" type="checkbox" />
                                            <span class="custom-checkbox-text">{audio.name}</span>
                                            <a href={audio.mp3}>Enlace</a>

                                        </label>
                                    </>
                                ))}
                                <Button variant="contained" color="primary">I</Button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}
