import { addAudio, deleteAudio, getAudios } from '../../helpers';
import React, { useState } from 'react';


export const Audio = ({ audio }) => {

    const [checkAudio, setCheckAudio] = useState(true);

    const addToArray = () => {
        if (checkAudio === false) {
            deleteAudio(audio._id);
            setCheckAudio(true);
        }
        else {
            addAudio(audio);
            setCheckAudio(false);
        }

    }

    return (
        <div className="custom-checkbox" onChange={addToArray}  >
            <label className="custom-checkbox">
                <input value={audio.mp3} type="hidden" name={audio.name} value="False" />
                <input key={audio._id} class="custom-checkbox-input" name={audio.name} value="True" type="checkbox" />
                <h3 className="custom-checkbox-text">{audio.name}</h3>
            </label>
        </div>
    )

}
