import { CategoriesElements } from '../../components/Categories/CategoriesElements';
import { SearchBox } from '../../components/Categories/SearchBox';
import { Sidebar } from '../../components/Sidebar/Sidebar';
import { Tupase } from '../../components/Categories/Tupase';

import { Switch, Link, Router } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
import slugify from 'slugify';

export const CreatorAuthor = () => {

    const [author, setAuthor] = useState([]);
    const [error, setError] = useState("");

    useEffect(() => {
        getAuthor();
    }, [])


    // A MANO
    const requestOptions = {
        method: 'GET',
        headers: {
            'Accept': 'application/json',
            'Access-Control-Allow-Origin': '*',
            'Content-Type': 'applications/json',
            'Mode': 'no-cors'
        }
    };

    const getAuthor = () => {
        return fetch("http://localhost:3002/api/tipo/show", {
            method: "GET",
        })
            .then((response) => {
                return response.json();
            })
            .then((data) => {
                if (data.error) {
                    setError(data.error);
                } else {
                    setAuthor(data);
                }
            })
            .catch((error) => {
                console.log(error);
            });
    };

    return (
        <>
            <div className="creator">
                <Sidebar />
                <Tupase children={"MARTINEZARES"} />
                <button onclick={console.log(author)}></button>
            </div>
        </>
    );
}
